mapper.update:
update member
set pwd = #{memberModifyDto.pwd},
name = #{memberModifyDto.name},
nick_name = #{memberModifyDto.nickName},
pho_no = #{memberModifyDto.phoNo}
where id = #{id}

mapper.select:
select email, pwd, name, nick_name, pho_no
from member
where id = #{id}

select role
from member
where id = #{id}

mapper.update:
UPDATE member
SET role = 1
WHERE id = #{id}
