mapper.select:
select name as petName, age as petAge, breed as petBreed, gender as petGender, reg_no as petRegNo
from member_pet
where member_id = #{id}

mapper.update:
update member_pet
set name = #{modifyParam.petName},
    age = #{modifyParam.petAge},
    breed = #{modifyParam.petBreed},
    gender = #{modifyParam.petGender}
where member_id = #{memberId} and name = #{name}
