mapper.select:
select name, login_id, password, pho_no, email, nick_name
from member
where id = #{id}

select id, name, login_id, password
from member
where login_id = #{id}

select login_id
from member
where name = #{name} and email = #{email};

select id, password
from member
where login_id = #{id} and email = #{email};
